const hbMenu=document.querySelector(".hb-menu");function closeMenu(e){"SUMMARY"==e.target.tagName&&e.preventDefault(),setTimeout((function(){hbMenu.style.width="0vw",setTimeout((function(){hbMenu.removeAttribute("open"),hbMenu.removeAttribute("style")}),300)}),100)}function addListeners(){hbMenu.open?document.addEventListener("click",closeMenu):document.removeEventListener("click",closeMenu)}hbMenu.addEventListener("toggle",addListeners);